client_script:
frappe.web_form.on("flight", (field, value) => {
    if (value) {
        // Fetch the flight details using frappe.call
        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Airplane Flight",
                name: value, // The selected flight
            },
            callback: function (response) {
                if (response.message) {
                    const flight = response.message;

                    // Auto-fill the passenger field
                    frappe.web_form.set_value("passenger", flight.passenger || "N/A");

                    // Auto-fill the price field
                    frappe.web_form.set_value("price", flight.flight_price || 0);
                } else {
                    frappe.msgprint("Flight details not found.");
                }
            },
        });
    }
});
