css:
.receipt-container {
       width: 100%;
       max-width: 600px;
       border: 2px solid #333;
       padding: 20px;
       font-family: Arial, sans-serif;
       background-color: #f9f9f9;
       border-radius: 10px;
   }
   .receipt-header {
       text-align: center;
       font-size: 20px;
       font-weight: bold;
       margin-bottom: 10px;
   }
   .receipt-details {
       font-size: 14px;
       line-height: 1.5;
   }
   .receipt-footer {
       text-align: center;
       font-size: 12px;
       font-style: italic;
       margin-top: 15px;
   }
   .highlight {
       font-weight: bold;
       color: #007bff;
   }
